package io.gardenerframework.camellia.authentication.server.client;

import io.gardenerframework.camellia.authentication.server.client.schema.response.ClientAppearance;
import lombok.NonNull;
import org.springframework.core.convert.converter.Converter;
import org.springframework.lang.Nullable;
import org.springframework.security.oauth2.server.authorization.client.RegisteredClient;
import org.springframework.stereotype.Component;

@Component
public class DemoClientVoConverter implements Converter<RegisteredClient, ClientAppearance> {
    @Nullable
    @Override
    public ClientAppearance convert(@NonNull RegisteredClient source) {
        return new ClientAppearance(source.getClientId(), source.getClientName(),
                "测试用的客户端，主要是用于第三方对接和测试时使用",
                "data:image/png;base64,");
    }
}
